import React, { useState } from 'react';
import Portugal from './portugal';
import { RadioSVGMap } from 'react-svg-map';
import './style.scss';
import { useDispatch, useSelector } from 'react-redux';
import { get } from '../../services/api';
import Forecast from '../Forecast';

export default function Map() {
	const [tooltipStyle, setToolTipStyle] = useState({ display: 'none' });
	const [pointedLocation, setPointedLocation] = useState(null);
	const [error, setError] = useState(false);

	const dispatch = useDispatch();

	const index = useSelector((state) => {
		return state.ipma.mapDay;
	});

	const locationCodes = {
		Aveiro: 1010500,
		Beja: 1020500,
		Braga: 1030300,
		Bragança: 1040200,
		'Castelo Branco': 1050200,
		Coimbra: 1060300,
		Évora: 1070500,
		Faro: 1080500,
		Guarda: 1090700,
		Leiria: 1100900,
		Lisboa: 1110600,
		Portalegre: 1121400,
		Porto: 1131200,
		Santarém: 1141600,
		Setúbal: 1151200,
		'Viana do Castelo': 1160900,
		'Vila Real': 1171400,
		Viseu: 1182300,
	};

	function getLocationName(event) {
		return event.target.attributes.name.value;
	}

	function getLocationInfo(pointedLocation, updateForecast = false) {
		get(
			`http://api.ipma.pt/open-data/forecast/meteorology/cities/daily/${locationCodes[pointedLocation]}.json`,
			{}
		)
			.then((data) => {
				setPointedLocation(data.data[index]);
				if (updateForecast) {
					dispatch({
						type: 'SET_FORECAST',
						forecast: data,
					});
					dispatch({
						type: 'SET_LOCAL',
						local: pointedLocation,
					});
				}
			})
			.catch((error) => {
				setError(true);
			});
	}

	function handleChange(selectedNode) {
		getLocationInfo(selectedNode.attributes.name.value, true);
	}

	function handleLocationMouseOver(event) {
		const pointedLocation = getLocationName(event);

		getLocationInfo(pointedLocation);
	}

	function handleLocationMouseOut() {
		setPointedLocation(null);
		setToolTipStyle({ display: 'none' });
	}

	function handleLocationMouseMove(event) {
		const tooltipStyle = {
			display: 'block',
			top: event.clientY + 20,
			left: event.clientX - 140,
		};
		setToolTipStyle(tooltipStyle);
	}

	return (
		<article>
			<div className={'svg__block__map--pt'}>
				<RadioSVGMap
					map={Portugal}
					locationClassName={(el) => {
						console.log(el);
						return 'svg';
					}}
					onLocationMouseOver={handleLocationMouseOver}
					onLocationMouseOut={handleLocationMouseOut}
					onLocationMouseMove={handleLocationMouseMove}
					onChange={handleChange}
				/>
				<div className='svg__block__map__tooltip' style={tooltipStyle}>
					{pointedLocation && (
						<Forecast
							pointedLocation={pointedLocation}
							style={{ backgroundColor: '#21255690' }}
							sm
						/>
					)}
				</div>
			</div>
		</article>
	);
}
