import React, { useEffect, useState, useRef } from 'react';
import Region from '../Region';
import { get } from '../../services/api';
import { useSelector } from 'react-redux';
import {
	Wrapper,
	ContainerFluid,
	Container,
	Row,
	Column,
	Title,
	Search,
	Button,
	SubTitle,
} from '../../style';
import Forecast from '../Forecast';
import Map from '../Map';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
	faMapMarkerAlt,
	faSearch,
	faTemperatureHigh,
	faTemperatureLow,
	faCloudRain,
	faCalendarAlt,
} from '@fortawesome/free-solid-svg-icons';
import { createSelector } from 'reselect';
import { useDispatch } from 'react-redux';
import Slider from '@material-ui/core/Slider';
import { withStyles } from '@material-ui/core/styles';

export default function Main(props) {
	const [error, setError] = useState(false);
	const [search, setSearch] = useState('');
	const [coords, setCoords] = useState(null);
	const [nearLocation, setNearLocation] = useState(null);

	const inputEl = useRef(null);

	const dispatch = useDispatch();

	const forecast = useSelector((state) => {
		return state.ipma.forecast;
	});

	const local = useSelector((state) => {
		return state.ipma.local;
	});

	const index = useSelector((state) => {
		return state.ipma.mapDay;
	});

	const type = useSelector((state) => {
		return state.ipma.typeView;
	});

	const months = [
		'Janeiro',
		'Fevereio',
		'MarÃ§o',
		'Abril',
		'Maio',
		'Junho',
		'Julho',
		'Agosto',
		'Setembro',
		'Outubro',
		'Novembro',
		'Dezembro',
	];

	const selectRegions = createSelector(
		(state) => state.ipma.regions,
		(_, search) => search,
		(regions, search) => {
			let temp = [];
			if (regions) {
				regions.filter(function (el) {
					if (el.local.toLowerCase().includes(search.toLowerCase())) {
						temp.push(el);
					}
				});
			}
			return temp;
		}
	);

	function toRad(Value) {
		return (Value * Math.PI) / 180;
	}

	function calcCrow(lat1, lon1, lat2, lon2) {
		var R = 6371; // km
		var dLat = toRad(lat2 - lat1);
		var dLon = toRad(lon2 - lon1);
		var lat1 = toRad(lat1);
		var lat2 = toRad(lat2);

		var a =
			Math.sin(dLat / 2) * Math.sin(dLat / 2) +
			Math.sin(dLon / 2) *
				Math.sin(dLon / 2) *
				Math.cos(lat1) *
				Math.cos(lat2);
		var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
		var d = R * c;
		return d;
	}

	const CustomSlider = withStyles({
		root: {
			width: '75%',
		},
		markLabel: {
			color: '#a9a9a9',
		},
		markLabelActive: {
			color: '#f1f1f1',
		},
	})(Slider);

	Date.prototype.addDays = function (days) {
		var date = new Date(this.valueOf());
		date.setDate(date.getDate() + days);
		return date;
	};

	const currentDate = new Date();

	const marks = [
		{
			value: 1,
			label: currentDate.getDate(),
		},
		{
			value: 2,
			label:
				currentDate.addDays(1).getDate() +
				' ' +
				months[currentDate.addDays(1).getMonth()],
		},
		{
			value: 3,
			label: currentDate.addDays(2).getDate(),
		},
		{
			value: 4,
			label: currentDate.addDays(3).getDate(),
		},
		{
			value: 5,
			label: currentDate.addDays(4).getDate(),
		},
	];

	useEffect(() => {
		navigator.geolocation.getCurrentPosition(
			(position) => {
				setCoords(position.coords);
			},
			(err) => {
				console.log(err);
				setCoords('Blocked');
			}
		);
	}, []);

	useEffect(() => {
		if (coords) {
			setError(null);
			get('distrits-islands.json', {})
				.then((data) => {
					dispatch({
						type: 'SET_REGIONS',
						regions: data.data,
					});
					if (coords !== 'Blocked') {
						let dist = null;
						let temp_local = null;
						data.data.map((local) => {
							let temp = calcCrow(
								local.latitude,
								local.longitude,
								coords.latitude,
								coords.longitude
							);
							if (dist === null) {
								dist = temp;
								temp_local = local;
							} else if (Math.abs(temp) < Math.abs(dist)) {
								dist = temp;
								temp_local = local;
							}
						});
						setNearLocation(temp_local);
					} else {
						setNearLocation(data.data[0]);
					}
				})
				.catch((error) => {
					//Do something with error
					setError(true);
				});
		}
	}, [coords]);

	useEffect(() => {
		if (nearLocation) {
			get(
				`http://api.ipma.pt/open-data/forecast/meteorology/cities/daily/${nearLocation.globalIdLocal}.json`,
				{}
			)
				.then((data) => {
					console.log(nearLocation);
					dispatch({
						type: 'SET_FORECAST',
						forecast: data,
					});
					dispatch({
						type: 'SET_LOCAL',
						local: nearLocation.local,
					});
				})
				.catch((error) => {
					setError(true);
				});
		}
	}, [nearLocation]);

	const regions = useSelector((state) => selectRegions(state, search));

	return (
		<Wrapper>
			<ContainerFluid style={{}}>
				{forecast && (
					<Container>
						<Row
							style={{
								backgroundColor: '#f1f1f1',
								borderRadius: 25,
								paddingLeft: 15,
								paddingRight: 15,
								alignItems: 'center',
								margin: 5,
								justifyContent: 'space-between',
								width: '100%',
								maxHeight: 40,
								height: 40,
								textAlign: 'left',
							}}
						>
							<Search
								type='text'
								ref={inputEl}
								name='Search'
								onChange={(event) => {
									setSearch(event.target.value);
								}}
							/>
							<Button
								style={{
									backgroundColor: '#f1f1f1',
									color: '#212121',
									border: 'none',
									padding: 0,
									margin: 0,
								}}
								onClick={() => {
									setSearch(inputEl.current.value);
								}}
							>
								<FontAwesomeIcon icon={faSearch} />
							</Button>
						</Row>
						<Row
							style={{
								alignItems: 'flex-start',
								flexWrap: 'wrap',
							}}
						>
							{regions &&
								regions.map((region, i) => {
									return <Region key={i} region={region} />;
								})}
						</Row>
						<Row style={{ justifyContent: 'center' }}>
							{local && (
								<Title>
									<FontAwesomeIcon icon={faMapMarkerAlt} />{' '}
									{local}
								</Title>
							)}
						</Row>
						<Row
							style={{
								justifyContent: 'center',
								marginBottom: 20,
							}}
						>
							<SubTitle>
								PrevisÃµes meteorolÃ³gicas para os prÃ³ximos 5 dias
							</SubTitle>
						</Row>
						<Row
							style={{
								flexWrap: 'wrap',
								alignItems: 'center',
								justifyContent: 'center',
								textAlign: 'center',
							}}
						>
							{forecast &&
								forecast.data.map((forecast, j) => {
									return (
										<Forecast
											key={j}
											pointedLocation={forecast}
										/>
									);
								})}
						</Row>
						<Row style={{ flexWrap: 'wrap' }}>
							<Column>
								<Map />
							</Column>
							<Column
								style={{
									alignItems: 'flex-start',
									justifyContent: 'center',
								}}
							>
								<Row
									style={{
										width: '75%',
										flex: 'none',
										padding: '0.25em 1em',
										margin: '0.5em',
									}}
								>
									<FontAwesomeIcon
										icon={faCalendarAlt}
										style={{
											fontSize: 25,
											marginRight: 20,
											color: '#f1f1f1',
										}}
									/>{' '}
									<CustomSlider
										aria-label='pretto slider'
										defaultValue={index + 1}
										aria-labelledby='discrete-slider'
										marks={marks}
										step={1}
										min={1}
										max={5}
