import React, { useEffect, useState } from 'react';
import { Card, Row, Column } from '../../style';
import WheatherWinDirIcon from '../WheaterWinDirIcon';
import WheatherIcon from '../WheatherIcon';
import { WiStrongWind } from 'weather-icons-react';
import { WiRaindrop } from 'react-icons/wi';
import { useDispatch } from 'react-redux';

export default function Forecast(props) {
	const dispatch = useDispatch();

	const { pointedLocation, style } = props;

	const [weekDay, setWeekDay] = useState(null);
	const [day, setDay] = useState(null);
	const [month, setMonth] = useState(null);
	const [year, setYear] = useState(null);

	const weekDays = [
		'Domingo',
		'Segunda-feira',
		'Terça-feira',
		'Quarta-feira',
		'Quinta-feira',
		'Sexta-feira',
		'Sábado',
	];

	const months = [
		'Janeiro',
		'Fevereio',
		'Março',
		'Abril',
		'Maio',
		'Junho',
		'Julho',
		'Agosto',
		'Setembro',
		'Outubro',
		'Novembro',
		'Dezembro',
	];

	useEffect(() => {
		if (pointedLocation) {
			const temp = new Date(pointedLocation.forecastDate);
			setDay(temp.getDate());
			setMonth(temp.getMonth());
			setYear(temp.getFullYear());
			setWeekDay(weekDays[temp.getDay()]);
		}
	}, [pointedLocation]);
	if (pointedLocation) {
		return (
			<Card
				style={{
					...style,
					textAlign: 'center',
					padding: 20,
				}}
				onMouseEnter={() => {
					console.log(props);
					dispatch({
						type: 'SET_HOVER_FORECAST',
						hover: null,
					});
				}}
			>
				<Row
					tyle={{
						alignItems: 'center',
						margin: 0,
						padding: 0,
					}}
				>
					<Column
						style={{
							margin: 0,
							padding: 5,
							borderRadius: 25,
							backgroundColor: '#212544',
							height: 50,
							justifyContent: 'center',
						}}
					>
						{weekDay && weekDay} {day && day} de{' '}
						{month && months[month]}
					</Column>
				</Row>
				<Row
					style={{
						alignItems: 'center',
						justifyContent: 'space-around',
						marginTop: 10,
					}}
				>
					<Column style={{ alignItems: 'center', padding: 0 }}>
						<WheatherIcon
							wheatherIcon={pointedLocation.idWeatherType}
						/>
					</Column>
				</Row>
				<Row
					style={{
						alignItems: 'center',
						justifyContent: 'space-around',
					}}
				>
					<Column style={{ alignItems: 'center', padding: 0 }}>
						<Row
							style={{
								fontSize: 30,
								fontWeight: 100,
								marginTop: 10,
								marginBottom: 5,
							}}
						>
							{pointedLocation.tMax}º C
						</Row>
						<Row
							style={{
								fontSize: 20,
								fontWeight: 100,
								marginTop: 5,
								marginBottom: 10,
							}}
						>
							{pointedLocation.tMin}º C
						</Row>
					</Column>
				</Row>
				{!props.sm && (
					<div>
						<Row
							style={{
								alignItems: 'center',
								justifyContent: 'space-around',
							}}
						>
							<Column
								style={{ alignItems: 'center', padding: 0 }}
							>
								<Row style={{ alignItems: 'center' }}>
									<WiStrongWind size={50} />
								</Row>
							</Column>
							<Column
								style={{ alignItems: 'center', padding: 0 }}
							>
								<Row style={{ alignItems: 'center' }}>
									<WheatherWinDirIcon
										wheatherWinDir={
											pointedLocation.predWindDir
										}
									/>
									({pointedLocation.predWindDir})
								</Row>
							</Column>
						</Row>
						<Row
							style={{
								alignItems: 'center',
								justifyContent: 'space-around',
							}}
						>
							<Column
								style={{ alignItems: 'center', padding: 0 }}
							>
								<Row style={{ alignItems: 'center' }}>
									<WiRaindrop size={65} />
								</Row>
							</Column>
							<Column style={{ padding: 0 }}>
								<Row>
									<div
										style={{
											width: '100%',
											height: 20,
											borderRadius: 2,
											background: '#f1f1f180',
											color: '#212121',
											textAlign: 'center',
										}}
									>
										<div
											style={{
												width: `${pointedLocation.precipitaProb}%`,
												height: 20,
												borderRadius: 2,
												background: '#f1f1f1',
												color: '#212121',
											}}
										>
											<div
												style={{
													fontSize: 15,
													fontWeight: '100',
													paddingLeft: 10,
												}}
											>
												{pointedLocation.precipitaProb}%
											</div>
										</div>
									</div>
								</Row>
							</Column>
						</Row>
					</div>
				)}
			</Card>
		);
	}
}
