import React, { useEffect, useState, useRef } from 'react';
import Region from '../Region';
import { get } from '../../services/api';
import { useSelector } from 'react-redux';
import {
	Wrapper,
	ContainerFluid,
	Container,
	Row,
	Column,
	Title,
	Search,
	Button,
	SubTitle,
} from '../../style';
import Forecast from '../Forecast';
import Map from '../Map';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
	faMapMarkerAlt,
	faSearch,
	faTemperatureHigh,
	faTemperatureLow,
	faCloudRain,
	faCalendarAlt,
} from '@fortawesome/free-solid-svg-icons';
import { createSelector } from 'reselect';
import { useDispatch } from 'react-redux';

import { months, calcCrow, CustomSlider } from '../../utils';

export default function Main(props) {
	const [error, setError] = useState(false);
	const [search, setSearch] = useState('');
	const [coords, setCoords] = useState(null);
	const [nearLocation, setNearLocation] = useState(null);

	const inputEl = useRef(null);

	const dispatch = useDispatch();

	const forecast = useSelector((state) => {
		return state.ipma.forecast;
	});

	const local = useSelector((state) => {
		return state.ipma.local;
	});

	const index = useSelector((state) => {
		return state.ipma.mapDay;
	});

	const type = useSelector((state) => {
		return state.ipma.typeView;
	});

	const hoverForecast = useSelector((state) => {
		return state.ipma.hoverForecast;
	});

	const selectRegions = createSelector(
		(state) => state.ipma.regions,
		(_, search) => search,
		(regions, search) => {
			let temp = [];
			if (regions) {
				regions.filter(function (el) {
					if (el.local.toLowerCase().includes(search.toLowerCase())) {
						temp.push(el);
					}
				});
			}
			return temp;
		}
	);

	Date.prototype.addDays = function (days) {
		var date = new Date(this.valueOf());
		date.setDate(date.getDate() + days);
		return date;
	};

	const currentDate = new Date();

	const marks = [
		{
			value: 1,
			label:
				currentDate.addDays(0).getDate() +
				' de ' +
				months[currentDate.addDays(0).getMonth()],
		},
		{
			value: 2,
			label:
				currentDate.addDays(1).getDate() +
				' de ' +
				months[currentDate.addDays(1).getMonth()],
		},
		{
			value: 3,
			label:
				currentDate.addDays(2).getDate() +
				' de ' +
				months[currentDate.addDays(2).getMonth()],
		},
		{
			value: 4,
			label:
				currentDate.addDays(3).getDate() +
				' de ' +
				months[currentDate.addDays(3).getMonth()],
		},
		{
			value: 5,
			label:
				currentDate.addDays(4).getDate() +
				' de ' +
				months[currentDate.addDays(4).getMonth()],
		},
	];

	useEffect(() => {
		navigator.geolocation.getCurrentPosition(
			(position) => {
				setCoords(position.coords);
			},
			(err) => {
				console.log(err);
				setCoords('Blocked');
			}
		);
	}, []);

	useEffect(() => {
		if (coords) {
			setError(null);
			get('distrits-islands.json', {})
				.then((data) => {
					dispatch({
						type: 'SET_REGIONS',
						regions: data.data,
					});
					if (coords !== 'Blocked') {
						let dist = null;
						let temp_local = null;
						data.data.map((local) => {
							let temp = calcCrow(
								local.latitude,
								local.longitude,
								coords.latitude,
								coords.longitude
							);
							if (dist === null) {
								dist = temp;
								temp_local = local;
							} else if (Math.abs(temp) < Math.abs(dist)) {
								dist = temp;
								temp_local = local;
							}
						});
						setNearLocation(temp_local);
					} else {
						setNearLocation(data.data[0]);
					}
				})
				.catch((error) => {
					//Do something with error
					setError(true);
				});
		}
	}, [coords]);

	useEffect(() => {
		if (nearLocation) {
			get(
				`http://api.ipma.pt/open-data/forecast/meteorology/cities/daily/${nearLocation.globalIdLocal}.json`,
				{}
			)
				.then((data) => {
					console.log(nearLocation);
					dispatch({
						type: 'SET_FORECAST',
						forecast: data,
					});
					dispatch({
						type: 'SET_LOCAL',
						local: nearLocation.local,
					});
				})
				.catch((error) => {
					setError(true);
				});
		}
	}, [nearLocation]);

	const regions = useSelector((state) => selectRegions(state, search));

	return (
		<Wrapper>
			{forecast && (
				<ContainerFluid style={{}}>
					<Container
						style={{
							backgroundColor: 'transparent',
						}}
					>
						<Row className={'jc-c'}>
							<img
								src={
									'http://www.ipma.pt/system/modules/ipma.website/resources/images/logo-ipma-17-180x48.png'
								}
								width='300'
							></img>
						</Row>
						<Row className={'jc-c mt-30'}>
							<h5
								className={'ta-c'}
								style={{
									width: '50%',
									fontSize: 15,
									fontWeight: 100,
								}}
							>
								Este website foi desenvolvido no ambito de um
								exercicio com fins de recrutamento. Os dados
								usados podem ser acedidos através da{' '}
								<a href='https://api.ipma.pt/' target='_blank'>
									{' '}
									API{' '}
								</a>{' '}
								do ipma.
							</h5>
						</Row>
					</Container>
					<Container>
						<Row
							className={'ph-20 m-10 br-25 ai-c jc-sb ta-l'}
							style={{
								backgroundColor: '#f1f1f1',
								width: '100%',
								maxHeight: 40,
								height: 40,
							}}
						>
							<Search
								type='text'
								ref={inputEl}
								name='Search'
								onChange={(event) => {
									setSearch(event.target.value);
								}}
							/>
							<Button
								className={'p-0 m-0'}
								style={{
									backgroundColor: '#f1f1f1',
									color: '#212121',
									border: 'none',
								}}
								onClick={() => {
									setSearch(inputEl.current.value);
								}}
							>
								<FontAwesomeIcon icon={faSearch} />
							</Button>
						</Row>
						<Row className={'ai-fs fw-w'}>
							{regions &&
								regions.map((region, i) => {
									return <Region key={i} region={region} />;
								})}
						</Row>
					</Container>
					<Container>
						<Row className={'jc-c'}>
							{local && (
								<Title>
									<FontAwesomeIcon icon={faMapMarkerAlt} />{' '}
									{local}
								</Title>
							)}
						</Row>
						<Row className={'jc-c mb-20'}>
							<SubTitle>
								Previsões meteorológicas para os próximos 5 dias
							</SubTitle>
						</Row>
						<Row className={'fw-w ai-c jc-c ta-c'}>
							{forecast &&
								forecast.data.map((forecast, j) => {
									return (
										<Forecast
											key={j}
											id={j}
											pointedLocation={forecast}
											style={{
												zIndex:
													hoverForecast === j
														? j + 1
														: 0,
												MsTransform:
													hoverForecast === j
														? 'scale(1.1)'
														: 'scale(1.0)',
												MozTransform:
													hoverForecast === j
														? 'scale(1.1)'
														: 'scale(1.0)',
												transform:
													hoverForecast === j
														? 'scale(1.1)'
														: 'scale(1.0)',
												WebkitTransform:
													hoverForecast === j
														? 'scale(1.1)'
														: 'scale(1.0)',
												OTransform:
													hoverForecast === j
														? 'scale(1.1)'
														: 'scale(1.0)',

												transition: 'all .5s ease',
												WebkitTransition:
													'all .5s ease',
												MozTransition: 'all .5s ease',
												cursor: 'pointer',
											}}
										/>
									);
								})}
						</Row>
					</Container>
					<Container>
						<Row className={'jc-c'}>
							{type && type === 'tMax' && (
								<Title>
									<FontAwesomeIcon
										className={'mr-10'}
										style={{
											fontSize: 25,
										}}
										icon={faTemperatureHigh}
									/>
									Previsões de temperatura máxima para o
									território continental
								</Title>
							)}
							{type && type === 'tMin' && (
								<Title>
									<FontAwesomeIcon
										className={'mr-10'}
										style={{
											fontSize: 25,
										}}
										icon={faTemperatureLow}
									/>
									Previsões de temperatura mínima para o
									território continental
								</Title>
							)}
							{type && type === 'precipit' && (
								<Title>
									<FontAwesomeIcon
										className={'mr-10'}
										style={{
											fontSize: 25,
										}}
										icon={faCloudRain}
									/>
									Previsões de probabilidade de precipitação
									para o território continental
								</Title>
							)}
						</Row>
						<Row className={'mt-30 fw-w'}>
							<Column>
								<Map />
							</Column>
							<Column className={'ai-fs jc-c'}>
								<Row
									className={'ph-20 pv-20 m-10'}
									style={{
										width: '75%',
										flex: 'none',
									}}
								>
									<FontAwesomeIcon
										icon={faCalendarAlt}
										className={'mr-30'}
										style={{
											fontSize: 25,
											color: '#f1f1f1',
										}}
									/>{' '}
									<CustomSlider
										aria-label='pretto slider'
										defaultValue={index + 1}
										aria-labelledby='discrete-slider'
										marks={marks}
										step={1}
										min={1}
										max={5}
										onChange={(event, newValue) => {
											dispatch({
												type: 'SET_MAP_DAY',
												index: newValue - 1,
											});
										}}
									/>
								</Row>

								<Button
									className={'p-10 br-5 flex al-c'}
									style={{
										backgroundColor:
											type === 'tMax'
												? '#212544'
												: '#212552',
										borderWidth: '0.03em',
									}}
									onClick={() => {
										dispatch({
											type: 'SET_TYPE',
											typeView: 'tMax',
										});
									}}
								>
									<FontAwesomeIcon
										className={'mr-10'}
										style={{
											fontSize: 25,
										}}
										icon={faTemperatureHigh}
									/>
									Temperatura máxima
								</Button>
								<Button
									className={'p-10 br-5 flex ai-c'}
									style={{
										backgroundColor:
											type === 'tMin'
												? '#212544'
												: '#212552',
										borderWidth: '0.03em',
									}}
									onClick={() => {
										dispatch({
											type: 'SET_TYPE',
											typeView: 'tMin',
										});
									}}
								>
									<FontAwesomeIcon
										className={'mr-10'}
										style={{
											fontSize: 25,
										}}
										icon={faTemperatureLow}
									/>
									Temperatura mínima
								</Button>
								<Button
									className={'p-10 br-5 flex ai-c'}
									style={{
										backgroundColor:
											type === 'precipit'
												? '#212544'
												: '#212552',
										borderWidth: '0.03em',
									}}
									onClick={() => {
										dispatch({
											type: 'SET_TYPE',
											typeView: 'precipit',
										});
									}}
								>
									<FontAwesomeIcon
										className={'mr-10'}
										style={{
											fontSize: 25,
										}}
										icon={faCloudRain}
									/>
									Probabilidade de precipitação
								</Button>
							</Column>
						</Row>
					</Container>
				</ContainerFluid>
			)}
		</Wrapper>
	);
}
