import React, { useEffect, useState, useRef } from 'react';
import Region from '../Region';
import { get } from '../../services/api';
import { useSelector } from 'react-redux';
import {
	Wrapper,
	ContainerFluid,
	Container,
	Row,
	Column,
	Title,
	Search,
	Button,
	SubTitle,
} from '../../style';
import Forecast from '../Forecast';
import Map from '../Map';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt, faSearch } from '@fortawesome/free-solid-svg-icons';
import { createSelector } from 'reselect';
import { useDispatch } from 'react-redux';
import Slider from '@material-ui/core/Slider';

export default function Main(props) {
	const [error, setError] = useState(false);
	const [search, setSearch] = useState('');
	const [coords, setCoords] = useState(null);
	const [nearLocation, setNearLocation] = useState(null);

	const inputEl = useRef(null);

	const dispatch = useDispatch();

	const forecast = useSelector((state) => {
		return state.ipma.forecast;
	});

	const local = useSelector((state) => {
		return state.ipma.local;
	});

	const selectRegions = createSelector(
		(state) => state.ipma.regions,
		(_, search) => search,
		(regions, search) => {
			let temp = [];
			if (regions) {
				regions.filter(function (el) {
					if (el.local.toLowerCase().includes(search.toLowerCase())) {
						temp.push(el);
					}
				});
			}
			return temp;
		}
	);

	function toRad(Value) {
		return (Value * Math.PI) / 180;
	}

	function calcCrow(lat1, lon1, lat2, lon2) {
		var R = 6371; // km
		var dLat = toRad(lat2 - lat1);
		var dLon = toRad(lon2 - lon1);
		var lat1 = toRad(lat1);
		var lat2 = toRad(lat2);

		var a =
			Math.sin(dLat / 2) * Math.sin(dLat / 2) +
			Math.sin(dLon / 2) *
				Math.sin(dLon / 2) *
				Math.cos(lat1) *
				Math.cos(lat2);
		var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
		var d = R * c;
		return d;
	}

	const currentDate = new Date();

	const marks = [
		{
			value: currentDate.getDate(),
			label: '0째C',
		},
		{
			value: 20,
			label: '20째C',
		},
		{
			value: 37,
			label: '37째C',
		},
		{
			value: 100,
			label: '100째C',
		},
	];

	useEffect(() => {
		navigator.geolocation.getCurrentPosition(
			(position) => {
				setCoords(position.coords);
			},
			(err) => {
				console.log(err);
				setCoords('Blocked');
			}
		);
	}, []);

	useEffect(() => {
		if (coords) {
			setError(null);
			get('distrits-islands.json', {})
				.then((data) => {
					dispatch({
						type: 'SET_REGIONS',
						regions: data.data,
					});
					if (coords !== 'Blocked') {
						let dist = null;
						let temp_local = null;
						data.data.map((local) => {
							let temp = calcCrow(
								local.latitude,
								local.longitude,
								coords.latitude,
								coords.longitude
							);
							if (dist === null) {
								dist = temp;
								temp_local = local;
							} else if (Math.abs(temp) < Math.abs(dist)) {
								dist = temp;
								temp_local = local;
							}
						});
						setNearLocation(temp_local);
					} else {
						setNearLocation(data.data[0]);
					}
				})
				.catch((error) => {
					//Do something with error
					setError(true);
				});
		}
	}, [coords]);

	useEffect(() => {
		if (nearLocation) {
			get(
				`http://api.ipma.pt/open-data/forecast/meteorology/cities/daily/${nearLocation.globalIdLocal}.json`,
				{}
			)
				.then((data) => {
					console.log(nearLocation);
					dispatch({
						type: 'SET_FORECAST',
						forecast: data,
					});
					dispatch({
						type: 'SET_LOCAL',
						local: nearLocation.local,
					});
				})
				.catch((error) => {
					setError(true);
				});
		}
	}, [nearLocation]);

	const regions = useSelector((state) => selectRegions(state, search));

	return (
		<Wrapper>
			<ContainerFluid style={{}}>
				{forecast && (
					<Container>
						<Row
							style={{
								backgroundColor: '#f1f1f1',
								borderRadius: 25,
								paddingLeft: 15,
								paddingRight: 15,
								alignItems: 'center',
								margin: 5,
								justifyContent: 'space-between',
								width: '100%',
								maxHeight: 40,
								height: 40,
								textAlign: 'left',
							}}
						>
							<Search
								type='text'
								ref={inputEl}
								name='Search'
								onChange={(event) => {
									setSearch(event.target.value);
								}}
							/>
							<Button
								style={{
									backgroundColor: '#f1f1f1',
									color: '#212121',
									border: 'none',
									padding: 0,
									margin: 0,
								}}
								onClick={() => {
									setSearch(inputEl.current.value);
								}}
							>
								<FontAwesomeIcon icon={faSearch} />
							</Button>
						</Row>
						<Row
							style={{
								alignItems: 'flex-start',
								flexWrap: 'wrap',
							}}
						>
							{regions &&
								regions.map((region, i) => {
									return <Region key={i} region={region} />;
								})}
						</Row>
						<Row style={{ justifyContent: 'center' }}>
							{local && (
								<Title>
									<FontAwesomeIcon icon={faMapMarkerAlt} />{' '}
									{local}
								</Title>
							)}
						</Row>
						<Row
							style={{
								justifyContent: 'center',
								marginBottom: 20,
							}}
						>
							<SubTitle>
								Previs천es meteorol처gicas para os pr처ximos 5 dias
							</SubTitle>
						</Row>
						<Row
							style={{
								flexWrap: 'wrap',
								alignItems: 'center',
								justifyContent: 'center',
								textAlign: 'center',
							}}
						>
							{forecast &&
								forecast.data.map((forecast, j) => {
									return (
										<Forecast
											key={j}
											pointedLocation={forecast}
										/>
									);
								})}
						</Row>
						<Row style={{ flexWrap: 'wrap' }}>
							<Column>
								<Map />
							</Column>
							<Column>
								<Slider
									defaultValue={1}
									aria-labelledby='discrete-slider'
									valueLabelDisplay='auto'
									step={1}
									min={1}
									max={5}
								/>
								<Button>T. Max</Button>
								<Button>T. Min</Button>
								<Button>Probabilidade de precipita챌찾o</Button>
							</Column>
						</Row>
						{error && <div>Ups! Something was wrong!</div>}
					</Container>
				)}
			</ContainerFluid>
		</Wrapper>
	);
}
